import React,  {useState, useEffect} from 'react';
import './App.css';

function App() {
  const isEmpty = " ";
  const hasPerson = "@";
  const theEnd = "X"
  const path = "#"


  const [gameMap, setGameMap] = useState([[]]);
  const [actualPositionOfPersonX, setActualPositionOfPersonX] = useState(0);
  const [actualPositionOfPersonY, setActualPositionOfPersonY] = useState(1);

  const gamePlanInString = `
@########  #
      #    #
######### ##
##   #     #
##   ###X###
`;



  useEffect(() => {
    let tempGameMap = gamePlanInString.split("\n");
    tempGameMap = tempGameMap.map(rowItem => {
      return rowItem.split("");
    });
    console.log("final tempGameMap", tempGameMap);
    setGameMap(tempGameMap);
    findPositionOfPersonInGameMapAndSetToState(gameMap);      
  }, []);

//  console.log(gamePlanInString);

  const findPositionOfPersonInGameMapAndSetToState = (gameMap) => {
    gameMap.map((rowItem, rowIndex) => {
      rowItem.map((cellItem, cellIndex) => {
        if(cellItem === "@") {
          setActualPositionOfPersonX(rowIndex);
          setActualPositionOfPersonY(cellIndex);
        }
      });
    });
  }

//  console.log(parseStringToGameMap(gamePlanInString));



  // console.log(gameMap);

  const handleMovePerson = (rowIndexToChecked, cellIndexToChecked) => {
    console.log({
      rowIndexToChecked, cellIndexToChecked
    },
    isAllowedMove(rowIndexToChecked, cellIndexToChecked)
    );
    if(!isAllowedMove(rowIndexToChecked, cellIndexToChecked)) {return;}
/*
    setActualPositionOfPersonX(cellIndexToChecked);
    setActualPositionOfPersonX(rowIndexToChecked);
    const countingMaxX = gameMap.map((rowItem, rowIndex) => (
      rowItem.map((cellItem, cellIndex) => {
        if (cellIndex > rowIndex)
          return cellIndex;

      })
    ));
*/

    setActualPositionOfPersonX(cellIndexToChecked);
    setActualPositionOfPersonY(rowIndexToChecked);
    const test = gameMap.map((rowItem, rowIndex) => (
      rowItem.map((cellItem, cellIndex) => {
        if(rowIndexToChecked === rowIndex && cellIndexToChecked === cellIndex) {
          return hasPerson;
          } else return path
      })
    ));
   setGameMap(test);

  }




  const isAllowedMove = (rowIndexToChecked, cellIndexToChecked, cellItem) => {
    return (
      (
        ( actualPositionOfPersonX - 1 === cellIndexToChecked || actualPositionOfPersonX + 1 === cellIndexToChecked )
        && ( actualPositionOfPersonY === rowIndexToChecked )
      )
      || (
        ( actualPositionOfPersonY - 1 === rowIndexToChecked || actualPositionOfPersonY + 1 === rowIndexToChecked )
        && ( actualPositionOfPersonX === cellIndexToChecked )
        )
      && (
        cellItem === "#" 
        )
      );
  }



  //return (<div>aaaaa</div>)

  return (
    <div className="App">
      <p>panacek je na pozici {actualPositionOfPersonY}, {actualPositionOfPersonX}</p>
        <table>
          <tbody>
            {gameMap.map((rowItem, rowIndex) => (<tr key={rowIndex}>
              {rowItem.map((cellItem, cellIndex) => (
                <td
                  key={cellIndex}
                  onClick={e=>handleMovePerson(rowIndex, cellIndex)}
                  >
                    {cellItem}-{rowIndex},{cellIndex}
                </td>
              ))}
            </tr>
            ))}
          </tbody>
        </table>
    </div>
  );
}


export default App;
