import React,  {useState} from 'react';
import './App.css';

function App() {
  const isEmpty = "o";
  const hasPerson = "x";

  const [gameMap, setGameMap] = useState(
    [
    [hasPerson, isEmpty, isEmpty],
    [isEmpty, isEmpty, isEmpty],
    [isEmpty, isEmpty, isEmpty],
    [isEmpty, isEmpty, isEmpty],
   ]
  );

  const gamePlan = `
    @##########  #
    #   #   #    #
    ########### ##
    #  #   #     #
    ## #   ###X###
`;


  console.log({gamePlan});

  const [actualPositionOfPersonX, setActualPositionOfPersonX] = useState(0);
  const [actualPositionOfPersonY, setActualPositionOfPersonY] = useState(0);

  // console.log(gameMap);

  const handleMovePerson = (rowIndexToChecked, cellIndexToChecked) => {
    if(!isAllowedMove(rowIndexToChecked, cellIndexToChecked)) {return;}


    setActualPositionOfPersonX(cellIndexToChecked);
    setActualPositionOfPersonY(rowIndexToChecked);
    const test = gameMap.map((rowItem, rowIndex) => (
      rowItem.map((cellItem, cellIndex) => {
        if(rowIndexToChecked === rowIndex && cellIndexToChecked === cellIndex) {
          return hasPerson;
          } else {return isEmpty}
      })
    ));
   setGameMap(test);

  }

  const isAllowedMove = (rowIndexToChecked, cellIndexToChecked) => {
    return (
      (
        ( actualPositionOfPersonX - 1 === cellIndexToChecked || actualPositionOfPersonX + 1 === cellIndexToChecked )
        && ( actualPositionOfPersonY === rowIndexToChecked )
      )
      || (
        ( actualPositionOfPersonY - 1 === rowIndexToChecked || actualPositionOfPersonY + 1 === rowIndexToChecked )
        && ( actualPositionOfPersonX === cellIndexToChecked )
        )
      );
  }


  return (
    <div className="App">
      <p>panacek je na pozici {actualPositionOfPersonY}, {actualPositionOfPersonX}</p>
        <table>
          <tbody>
        {gameMap.map((rowItem, rowIndex) => (<tr key={rowIndex}>
          {rowItem.map((cellItem, cellIndex) => (
            <td
              key={cellIndex}
              onClick={e=>handleMovePerson(rowIndex, cellIndex)}
              >
                {cellItem}-{rowIndex},{cellIndex}
            </td>
          ))}
        </tr>
        ))}
          </tbody>
        </table>
    </div>
  );
}


export default App;
