import React,  {useState} from 'react';
import './App.css';

function App() {
  const isEmpty = "o";
  const hasPerson = "x";

  const [gameMap, setGameMap] = useState(
    [
    [isEmpty, isEmpty, isEmpty],
    [isEmpty, isEmpty, isEmpty],
    [isEmpty, isEmpty, isEmpty],
    [isEmpty, isEmpty, isEmpty],
   ]
  );

  console.log(gameMap);

  const handleMovePerson = (rowIndex, cellIndex) => {


    setGameMap(
    [
    [isEmpty, isEmpty, isEmpty],
    [isEmpty, isEmpty, isEmpty],
    [isEmpty, hasPerson, isEmpty],
    [isEmpty, isEmpty, isEmpty],
   ]

      );
    console.log("AAAAAAAAAAA", rowIndex, cellIndex, gameMap);
  }

  return (
    <div className="App">
        <table>
          <tbody>
        {gameMap.map((rowItem, rowIndex) => (<tr key={rowIndex}>
          {console.log({rowItem})}
          {rowItem.map((cellItem, cellIndex) => (
            <td
              key={cellIndex}
              onClick={e=>handleMovePerson(rowIndex, cellIndex)}
              >
                {cellItem}-{rowIndex},{cellIndex}
            </td>
          ))}
        </tr>
        ))}
          </tbody>
        </table>
    </div>
  );
}


export default App;
